mapper.select:
SELECT enrd_id, mem_id , mem_phone, mem_name, mem_email, mem_company_name, slb_name, enrd_stat, enrd_join_date, enrd_complete_date FROM (select ROW_NUMBER() OVER (ORDER BY m.mem_join_date) NUM,
from enrollment_detail E LEFT JOIN member M ON e.mem_id = m.mem_id LEFT JOIN course C ON e.crs_id = c.crs_id LEFT JOIN syllabus S ON c.slb_id = s.slb_id ORDER BY enrd_id) WHERE NUM BETWEEN #{pageStart}
AND #{perPageNum}
AND (#{perPageNum} + #{pageStart}) -1
order by enrd_id
